import { useEffect, useState } from "react";
import axios from "axios";
import { Link } from "react-router-dom";

const Forum = () => {
  const [posts, setPosts] = useState([]);
  const [newTopicTitle, setNewTopicTitle] = useState("");
  const [newTopicDescription, setNewTopicDescription] = useState("");
  const [isCreating, setIsCreating] = useState(false);
  const user = JSON.parse(localStorage.getItem("user"));
  const [username, setUsername] = useState("");

  useEffect(() => {
    axios.get("http://localhost:3001/topics").then((response) => {
      setPosts(response.data);
    });
  }, []);

  const createTopic = async () => {
    if (!newTopicTitle || !newTopicDescription) {
      alert("Будь ласка, заповніть усі поля!");
      return;
    }
  
    if (!user || !user.id) {
        alert("Ви не авторизовані!");
        return;
      }
  
    try {
      const response = await axios.post("http://localhost:3001/createTopic", {
        username: username,
        title: newTopicTitle,
        description: newTopicDescription,
        user_id: user.id // передайте user_id
      });
      
      console.log("Тема створена:", response.data);
      setIsCreating(false);
      setNewTopicTitle("");
      setNewTopicDescription("");
    } catch (error) {
      console.error("Помилка при створенні теми:", error.response?.data || error.message);
    }
  };
  
  

  return (
    <div className="forum-container">
      <div className="sidebar">
        {isCreating && (
          <div className="create-topic">
            <input
              type="text"
              value={newTopicTitle}
              onChange={(e) => setNewTopicTitle(e.target.value)}
              placeholder="Назва нової теми"
            />
            <input
              type="text"
              value={newTopicDescription}
              onChange={(e) => setNewTopicDescription(e.target.value)}
              placeholder="Опис теми"
            />
            <button onClick={createTopic}>Створити</button>
            <button onClick={() => setIsCreating(false)}>Скасувати</button>
          </div>
        )}
      </div>
  
      {!isCreating && (
        
        <div>
          <div className="button-create">
            <button className="button-post-create" onClick={() => setIsCreating(true)}>
            Створити нову тему
            </button>
          </div>
          <h1>THEMES</h1>
          <div className="forum-box">
            {posts.length === 0 ? (
              <p>Немає жодної теми на форумі.</p>
            ) : (
              posts.map((post) => (
                <div key={post.id} className="post-item">
                  <div className="post-header">
                    <span className="post-author">{post.username || "Unknown Author"}</span>
                    <span className="post-date">
                      {new Date(post.created_at).toLocaleDateString()}
                    </span>
                  </div>
                  <Link to={`/forum/topic/${post.id}`}>
                    <h3>{post.title}</h3>
                  </Link>
                  <p>{post.content?.slice(0, 80)}...</p>
                </div>
              ))
            )}
          </div>
        </div>
      )}
    </div>
  );
  
};

export default Forum;
