import { useParams } from "react-router-dom";
import { useEffect, useState } from "react";
import axios from "axios";

const TopicPage = () => {
  const { id } = useParams();
  const [topic, setTopic] = useState(null);
  const [comments, setComments] = useState([]);

  useEffect(() => {
    axios.get(`http://localhost:3001/topics/${id}`).then((res) => {
      setTopic(res.data);
    });
    axios.get(`http://localhost:3001/topics/${id}/comments`).then((res) => {
      setComments(res.data);
    });
  }, [id]);

  if (!topic) return <p>Завантаження теми...</p>;

  return (
    <div className="topic-page">
      <h2>{topic.title}</h2>
      <p>{topic.content}</p>
      <h className='comments-title'>Коментарі:</h>
      {comments.length === 0 ? (
        <p className="comments">Коментарі відсутні.</p>
      ) : (
        comments.map((c, i) => (
          <div key={i} className="comment">
            <p>{c.text}</p>
          </div>
        ))
      )}
    </div>
  );
};

export default TopicPage;
