import { useEffect, useState } from "react";
import axios from "axios";
import { Link } from "react-router-dom";

const Forum = () => {
  const [posts, setPosts] = useState([]);
  const [newTopicTitle, setNewTopicTitle] = useState("");
  const [newTopicDescription, setNewTopicDescription] = useState("");
  const [isCreating, setIsCreating] = useState(false);
  const [userNames, setUserNames] = useState({}); // Стейт для збереження імен користувачів

  const user = JSON.parse(localStorage.getItem("user"));
  const username = user ? user.username : "Unknown User"; // Отримуємо ім'я з LocalStorage

  useEffect(() => {
    // Отримуємо теми
    axios.get("http://localhost:3001/topics")
      .then((response) => {
        setPosts(response.data);
        
        // Для кожної теми отримуємо ім'я користувача
        response.data.forEach((post) => {
          axios.get(`http://localhost:3001/users/${post.user_id}`)
            .then((userResponse) => {
              setUserNames((prevNames) => ({
                ...prevNames,
                [post.user_id]: userResponse.data.name // Зберігаємо ім'я користувача по user_id
              }));
            })
            .catch((err) => console.error("Error fetching user:", err));
        });
      })
      .catch((error) => console.error("Error fetching topics:", error));
  }, []);

  const deletePost = async (postId) => {
    if (!window.confirm("Ви впевнені, що хочете видалити цю тему?")) return;
  
    try {
      await axios.delete(`http://localhost:3001/topics/${postId}`);
      setPosts((prev) => prev.filter((post) => post.id !== postId));
    } catch (err) {
      console.error("Помилка при видаленні:", err);
      alert("Не вдалося видалити тему.");
    }
  };

  const createTopic = async () => {
    if (!newTopicTitle || !newTopicDescription) {
      alert("Будь ласка, заповніть усі поля!");
      return;
    }

    if (!user || !user.id) {
      alert("Ви не авторизовані!");
      return;
    }

    try {
      const response = await axios.post("http://localhost:3001/createTopic", {
        username: username,
        title: newTopicTitle,
        description: newTopicDescription,
        user_id: user.id,
      });

      setNewTopicTitle("");
      setNewTopicDescription("");
      setIsCreating(false);
    } catch (error) {
      console.error("Помилка при створенні теми:", error.response?.data || error.message);
    }
  };

  return (
    <div className="forum-container">
      <div className="sidebar">
        {isCreating && (
          <div className="create-topic">
            <input
              type="text"
              value={newTopicTitle}
              onChange={(e) => setNewTopicTitle(e.target.value)}
              placeholder="Назва нової теми"
            />
            <input
              type="text"
              value={newTopicDescription}
              onChange={(e) => setNewTopicDescription(e.target.value)}
              placeholder="Опис теми"
            />
            <button onClick={createTopic}>Створити</button>
            <button onClick={() => setIsCreating(false)}>Скасувати</button>
          </div>
        )}
      </div>

      {!isCreating && (
        <div>
          <h1>THEMES</h1>
          <div className="forum-box">
            {posts.length === 0 ? (
              <p>Немає жодної теми на форумі.</p>
            ) : (
              posts.map((post) => (
                <div key={post.id} className="post-item">
                  <div className="post-header">
                    <span className="post-author">{post.username || "Unknown Author"}</span>
                    <span className="post-date">
                      {new Date(post.created_at).toLocaleDateString()}
                    </span>
                  </div>
                  <Link to={`/forum/topic/${post.id}`}>
                    <h3>{post.title}</h3>
                  </Link>
                  <p>{post.content?.slice(0, 80)}...</p>

                  {(user?.username === post.username || user?.username === "admin") && (
                    <button
                      className="delete-post-button"
                      onClick={() => deletePost(post.id)}
                    >
                      🗑️ Видалити тему
                    </button>
                  )}
                </div>
              ))
            )}
          </div>
          <button className="button-post-create" onClick={() => setIsCreating(true)}>
            Створити нову тему
          </button>
        </div>
      )}
    </div>
  );
};

export default Forum;
