import React from "react";
import { useParams, useNavigate } from "react-router-dom";

const planetInfo = {
  mercury: "Mercury is the smallest planet in the Solar System.",
  venus: "Venus has a thick atmosphere and the hottest surface.",
  earth: "Earth is the only planet known to support life.",
  mars: "Mars is known as the Red Planet.",
  jupiter: "Jupiter is the largest planet in the Solar System.",
  saturn: "Saturn is famous for its rings.",
  uranus: "Uranus rotates on its side.",
  neptune: "Neptune is the farthest planet from the Sun.",
  pluto: "Pluto is a dwarf planet in the Kuiper Belt.",
};

const PlanetPage = () => {
  const { planetName } = useParams();
  const navigate = useNavigate();

  return (
    <div>
      <h1>{planetName.charAt(0).toUpperCase() + planetName.slice(1)}</h1>
      <p>{planetInfo[planetName] || "No information available."}</p>
      <button onClick={() => navigate(-1)}>Назад</button>
    </div>
  );
};

export default PlanetPage;
