import { useEffect, useState } from "react";
import { io } from "socket.io-client";
import axios from "axios";

const socket = io("http://localhost:3001");

const Forum = () => {
  const [topics, setTopics] = useState([]);
  const [user, setUser] = useState(null);
  const [newTopicTitle, setNewTopicTitle] = useState("");
  const [newTopicContent, setNewTopicContent] = useState("");
  const [newComment, setNewComment] = useState("");
  const [isRegistering, setIsRegistering] = useState(false);

  const [registerData, setRegisterData] = useState({
    username: "",
    email: "",
    password: "",
  });
  const [loginData, setLoginData] = useState({
    email: "",
    password: "",
  });

  useEffect(() => {
    axios.get("http://localhost:3001/topics").then((res) => {
      setTopics(res.data);
    });

    socket.on("newTopic", (topic) => {
      setTopics((prev) => [topic, ...prev]);
    });

    socket.on("newComment", (comment) => {
      setTopics((prev) => 
        prev.map(topic => topic.id === comment.topicId
          ? { ...topic, comments: [...topic.comments, comment] }
          : topic
        )
      );
    });

    return () => {
      socket.off("newTopic");
      socket.off("newComment");
    };
  }, []);

  useEffect(() => {
    const savedUser = localStorage.getItem("user");
    if (savedUser) {
      setUser(JSON.parse(savedUser));
    }
  }, []);

  const register = async () => {
    try {
      const res = await axios.post("http://localhost:3001/register", registerData);
      setUser(res.data);
      localStorage.setItem("user", JSON.stringify(res.data));
      setRegisterData({ username: "", email: "", password: "" });
      alert("Registration successful!");
    } catch (error) {
      console.error("Registration error:", error.response?.data || error.message);
    }
  };

  const login = async () => {
    try {
      const res = await axios.post("http://localhost:3001/login", loginData);
      setUser(res.data);
      localStorage.setItem("user", JSON.stringify(res.data));
      setLoginData({ email: "", password: "" });
      alert("Login successful!");
    } catch (error) {
      console.error("Login error:", error.response?.data || error.message);
    }
  };

  const logout = () => {
    setUser(null);
    localStorage.removeItem("user");
  };

  const createTopic = () => {
    if (newTopicTitle.trim() && newTopicContent.trim() && user) {
      const topic = {
        title: newTopicTitle,
        content: newTopicContent,
        userId: user.id,
        username: user.username,
      };
      socket.emit("createTopic", topic);
      setNewTopicTitle("");
      setNewTopicContent("");
    }
  };

  const createComment = (topicId) => {
    if (newComment.trim() && user) {
      const comment = {
        text: newComment,
        topicId,
        userId: user.id,
        username: user.username,
      };
      socket.emit("createComment", comment);
      setNewComment("");
    }
  };

  return (
    <div className="forum-container">
      <h2>Форум про космос</h2>

      {user ? (
        <>
          <p>Logged in as: <strong>{user.username}</strong></p>
          <button className="btn-logout" onClick={logout}>Logout</button>

          <h3>Нова тема</h3>
          <input
            type="text"
            placeholder="Заголовок теми"
            value={newTopicTitle}
            onChange={(e) => setNewTopicTitle(e.target.value)}
          />
          <textarea
            placeholder="Вміст теми"
            value={newTopicContent}
            onChange={(e) => setNewTopicContent(e.target.value)}
          />
          <button onClick={createTopic}>Створити тему</button>

          <h3>Теми</h3>
          {topics.map((topic) => (
            <div key={topic.id} className="topic">
              <h4>{topic.title}</h4>
              <p>{topic.content}</p>
              <div>
                {topic.comments.map((comment, index) => (
                  <div key={index} className="comment">
                    <strong>{comment.username}:</strong> {comment.text}
                  </div>
                ))}
              </div>
              <input
                type="text"
                placeholder="Ваш коментар..."
                value={newComment}
                onChange={(e) => setNewComment(e.target.value)}
                onKeyDown={(e) => e.key === "Enter" && createComment(topic.id)}
              />
              <button onClick={() => createComment(topic.id)}>Відправити коментар</button>
            </div>
          ))}
        </>
      ) : (
        <>
          <h3>{isRegistering ? "Реєстрація" : "Вхід"}</h3>

          {isRegistering ? (
            <>
              <input
                type="text"
                placeholder="Ім'я"
                value={registerData.username}
                onChange={(e) => setRegisterData({ ...registerData, username: e.target.value })}
              />
              <input
                type="email"
                placeholder="Електронна пошта"
                value={registerData.email}
                onChange={(e) => setRegisterData({ ...registerData, email: e.target.value })}
              />
              <input
                type="password"
                placeholder="Пароль"
                value={registerData.password}
                onChange={(e) => setRegisterData({ ...registerData, password: e.target.value })}
              />
              <button onClick={register}>Зареєструватися</button>
            </>
          ) : (
            <>
              <input
                type="email"
                placeholder="Електронна пошта"
                value={loginData.email}
                onChange={(e) => setLoginData({ ...loginData, email: e.target.value })}
              />
              <input
                type="password"
                placeholder="Пароль"
                value={loginData.password}
                onChange={(e) => setLoginData({ ...loginData, password: e.target.value })}
              />
              <button onClick={login}>Увійти</button>
            </>
          )}

          <button onClick={() => setIsRegistering(!isRegistering)}>
            {isRegistering ? "Вже є акаунт? Увійти" : "Немає акаунту? Реєструйтесь"}
          </button>
        </>
      )}
    </div>
  );
};

export default Forum;
