import React from "react";
import "../style.css";
import { useEffect, useState } from "react";

import { useRef } from "react";

const Main = () => {
  const text = "Welcome!";
  const typingSpeed = 200;
  const [displayedText, setDisplayedText] = useState("");
  const [index, setIndex] = useState(0); 

  useEffect(() => {
    const sections = document.querySelectorAll(".info-section");
    const observer = new IntersectionObserver(
      (entries) => {
        entries.forEach((entry) => {
          if (entry.isIntersecting) {
            entry.target.classList.add("visible"); // додаємо, не замінюємо
          }
        });
      },
      { threshold: 0.2 }
    );
  
    sections.forEach((section) => observer.observe(section));
  
    return () => sections.forEach((section) => observer.unobserve(section));
  }, []);
  

  useEffect(() => {
    if (index < text.length) {
      const timeout = setTimeout(() => {
        setDisplayedText((prev) => prev + text[index]);
        setIndex(index + 1); 
      }, typingSpeed);

      return () => clearTimeout(timeout);
    }
  }, [index, text]);

  return (
    <div className="main-container">
      <div className="welcome-section">
        <h1 className="welcome-text">{displayedText}</h1>
      </div>
      <div className="additional-info">
        <p class = 'main-text'>Chronology of the Universe</p>

        {universeInfo.map((item, index) => (
          <div
            key={index}
            className={`info-section ${index % 2 !== 0 ? "reverse" : ""}`}
            ref={(el) => {
              if (el) observer.observe(el); // якщо використовуєш IntersectionObserver
            }}
          >
            <div className="info-text">
              <h2>{item.title}</h2>
              <p>{item.content}</p>
            </div>
            {item.image && (
              <img src={item.image} alt={item.title} className="info-image" />
            )}
          </div>
        ))}
      </div>
    </div>
  );
};


export default Main;


const universeInfo = [
  {
    title: 'Big Bang',
    content: 'The universe began from a singularity approximately 13.8 billion years ago.',
    image: '/assets/stars/b.png',
  },
  {
    title: 'Formation of Galaxies',
    content: 'After the Big Bang, matter began to clump together, forming galaxies.',
    image: '/assets/stars/b.png',
  },
  {
    title: 'The Solar System',
    content: 'About 4.6 billion years ago, the solar system formed around our Sun.',
    image: '/assets/stars/b.png',
  },
];
