import { useEffect, useState } from "react";
import axios from "axios";
import { Link } from "react-router-dom";

const Forum = () => {
  const [posts, setPosts] = useState([]);
  const [newTopicTitle, setNewTopicTitle] = useState("");
  const [newTopicDescription, setNewTopicDescription] = useState("");
  const [isCreating, setIsCreating] = useState(false);
  const user = JSON.parse(localStorage.getItem("user"));
  const [username, setUsername] = useState("");
  useEffect(() => {
    axios.get("http://localhost:3001/topics").then((response) => {
      setPosts(response.data);
    });
  }, []);
  const deletePost = async (postId) => {
    if (!window.confirm("–í–∏ –≤–ø–µ–≤–Ω–µ–Ω—ñ, —â–æ —Ö–æ—á–µ—Ç–µ –≤–∏–¥–∞–ª–∏—Ç–∏ —Ü—é —Ç–µ–º—É?")) return;
  
    try {
      await axios.delete(`http://localhost:3001/topics/${postId}`);
      setPosts((prev) => prev.filter((post) => post.id !== postId));
    } catch (err) {
      console.error("–ü–æ–º–∏–ª–∫–∞ –ø—Ä–∏ –≤–∏–¥–∞–ª–µ–Ω–Ω—ñ:", err);
      alert("–ù–µ –≤–¥–∞–ª–æ—Å—è –≤–∏–¥–∞–ª–∏—Ç–∏ —Ç–µ–º—É.");
    }
  };  
  const createTopic = async () => {
    if (!newTopicTitle || !newTopicDescription) {
      alert("–ë—É–¥—å –ª–∞—Å–∫–∞, –∑–∞–ø–æ–≤–Ω—ñ—Ç—å —É—Å—ñ –ø–æ–ª—è!");
      return;
    }
  
    if (!user || !user.id) {
        alert("–í–∏ –Ω–µ –∞–≤—Ç–æ—Ä–∏–∑–æ–≤–∞–Ω—ñ!");
        return;
      }
  
    try {
      const response = await axios.post("http://localhost:3001/createTopic", {
        username: username,
        title: newTopicTitle,
        description: newTopicDescription,
        user_id: user.id // –ø–µ—Ä–µ–¥–∞–π—Ç–µ user_id
      });
      
      console.log("–¢–µ–º–∞ —Å—Ç–≤–æ—Ä–µ–Ω–∞:", response.data);
      setUsername("")
      setIsCreating(false);
      setNewTopicTitle("");
      setNewTopicDescription("");
    } catch (error) {
      console.error("–ü–æ–º–∏–ª–∫–∞ –ø—Ä–∏ —Å—Ç–≤–æ—Ä–µ–Ω–Ω—ñ —Ç–µ–º–∏:", error.response?.data || error.message);
    }
  };
  
  

  return (
    <div className="forum-container">
      <div className="sidebar">
        {isCreating && (
          <div className="create-topic">
            <input
              type="text"
              value={newTopicTitle}
              onChange={(e) => setNewTopicTitle(e.target.value)}
              placeholder="–ù–∞–∑–≤–∞ –Ω–æ–≤–æ—ó —Ç–µ–º–∏"
            />
            <input
              type="text"
              value={newTopicDescription}
              onChange={(e) => setNewTopicDescription(e.target.value)}
              placeholder="–û–ø–∏—Å —Ç–µ–º–∏"
            />
            <button onClick={createTopic}>–°—Ç–≤–æ—Ä–∏—Ç–∏</button>
            <button onClick={() => setIsCreating(false)}>–°–∫–∞—Å—É–≤–∞—Ç–∏</button>
          </div>
        )}
      </div>
  
      {!isCreating && (
        
        <div>
          <h1> 
          </h1>
          <h1>THEMES</h1>
          <div className="forum-box">
          {posts.length === 0 ? (
            <p>–ù–µ–º–∞—î –∂–æ–¥–Ω–æ—ó —Ç–µ–º–∏ –Ω–∞ —Ñ–æ—Ä—É–º—ñ.</p>
          ) : (
            posts.map((post) => (
              <div key={post.id} className="post-item">
                <div className="post-header">
                  <span className="post-author">{post.username || "Unknown Author"}</span>
                  <span className="post-date">
                    {new Date(post.created_at).toLocaleDateString()}
                  </span>
                </div>
                <Link to={`/forum/topic/${post.id}`}>
                  <h3>{post.title}</h3>
                </Link>
                <p>{post.description?.slice(0, 200)}...</p>

                {(user?.username === post.username || user?.username === "admin") && (
                  <button
                    className="delete-post-button"
                    onClick={() => deletePost(post.id)}
                  >
                    üóëÔ∏è –í–∏–¥–∞–ª–∏—Ç–∏ —Ç–µ–º—É
                  </button>
                )}
              </div>
              
            ))
          )}
          </div>
          <button className="button-post-create" onClick={() => setIsCreating(true)}>
                  –°—Ç–≤–æ—Ä–∏—Ç–∏ –Ω–æ–≤—É —Ç–µ–º—É
          </button>
        </div>
      )}
    </div>
  );
  
};

export default Forum;
