import { useEffect, useState } from "react";
import axios from "axios";

const MoonPhase = () => {
  const [moonData, setMoonData] = useState(null);
  const [date, setDate] = useState(new Date().toISOString().split("T")[0]);

  useEffect(() => {
    axios
      .get(`http://localhost:3001/moonphase?startdate=${date}`)
      .then((response) => {
        if (response.data) {
          console.log("Moon phase data:", response.data);
          setMoonData(response.data); 
        } else {
          console.log("No data received");
        }
      })
      .catch((error) => {
        console.error("Error fetching moon phase:", error.response?.data || error.message);
      });
  }, [date]);
  
  
  

  return (
    <div className="moon">
      <div>
        <h2>Choose a date:</h2>
        <div class = "phases">
        <input type="date" value={date} onChange={(e) => setDate(e.target.value)} />
        <p><strong>Phase:</strong> {moonData.phase}: <p>Loading...</p></p>
        <img src={moonData.moon_image} alt="Moon Phase" />
        </div>
      </div>
    </div>
  );
};

export default MoonPhase;
