import { useEffect, useState } from "react";
import axios from "axios";

const Forum = () => {
  const [posts, setPosts] = useState([]);
  const [newTopic, setNewTopic] = useState("");
  const [isCreating, setIsCreating] = useState(false);

  useEffect(() => {
    axios.get("http://localhost:3001/topics").then((response) => {
      setPosts(response.data);
    });
  }, []);

  const createTopic = async () => {
    if (newTopic.trim()) {
      const response = await axios.post("http://localhost:3001/createTopic", {
        title: newTopic,
      });
      setPosts([response.data, ...posts]);
      setNewTopic("");
      setIsCreating(false);
    }
  };

  return (
    <div className="forum-container">
      <div className="sidebar">
        <button className="create-button" onClick={() => setIsCreating(true)}>
          Створити нову тему
        </button>
        {isCreating && (
          <div className="create-topic">
            <input
              type="text"
              value={newTopic}
              onChange={(e) => setNewTopic(e.target.value)}
              placeholder="Назва нової теми"
            />
            <button onClick={createTopic}>Створити</button>
            <button onClick={() => setIsCreating(false)}>Скасувати</button>
          </div>
        )}
      </div>

      <div className="forum-box">
        <h2>Темы форума</h2>
        <div className="forum-box">
          {posts.length === 0 ? (
            <p>Немає жодної теми на форумі. fkgldfkglklrgklwkglekrglkdrglkslfkglsdkfglkdfglkfl;kgfdlkgflkgflkflkgkgklslkkfkg</p>
          ) : (
            posts.map((post) => (
              <div key={post.id} className="post-item">
                <h3>{post.title}</h3>
                <p>{post.body}</p>
              </div>
            ))
          )}
        </div>
      </div>
    </div>
  );
};

export default Forum;
