import React, { useEffect, useState }  from "react";
import { BrowserRouter as Router, Routes, Route, Link } from "react-router-dom";
import Main from "./components/Main";
import Planets from "./components/Planets";
import Stars from "./components/Stars";
import Moon from "./components/Moon";
import Register from "./components/Register";
import About from "./components/About";
import PlanetPage from "./pages/PlanetPage";
import Forum from './components/Forum'
import ScrollProgressBar from "./components/ScrollProgressBar";
import TopicPage from "./components/TopicPage";
import "./style.css";
//npm start

function App() {
  const [highContrast, setHighContrast] = useState(false);

  useEffect(() => {
    document.body.classList.toggle("high-contrast", highContrast);
  }, [highContrast]);

  const toggleContrast = () => {
    setHighContrast(prev => !prev);
  };
  return (
    <Router>
      <div className="cosmos-back"></div>
      <div className="app-container">
        <ScrollProgressBar />
        <h1>Solar System Project</h1>
        <header>
          <nav className="nav-links">
            <Link to="/">Main</Link>
            <Link to="/planets">Planets</Link>
            <Link to="/stars">Stars</Link>
            <Link to="/moon">Moon</Link>
            <Link to="/register">Forum</Link>
            <Link to="/about">About</Link>
            <button onClick={toggleContrast} className="contrast-toggle" aria-label="Toggle high contrast mode">
              🌗 Contrast
            </button>
          </nav>
        </header>

        <div className="page-container">
          <Routes>
            <Route path="/" element={<Main />} />
            <Route path="/planets" element={<Planets />} />
            <Route path="/planets/:planetName" element={<PlanetPage />} />
            <Route path="/stars" element={<Stars />} />
            <Route path="/moon" element={<Moon />} />
            <Route path="/forum" element={<Forum />} />
            <Route path="/register" element={<Register />} />
            <Route path="/about" element={<About />} />
            <Route path="/forum/topic/:id" element={<TopicPage />} />
          </Routes>
        </div>

        <div class = "bottom">
          <p>All rights reserved (C)</p>
        </div>
      </div>
    </Router>
  );
}

export default App;
