import React, { useState } from "react";
import { Link } from "react-router-dom";
import "../style.css";
import planetsGif from "../assets/planetsfor.gif";

const planets = [
  { name: "Mercury", coords: "200,185,40", link: "/planets/mercury" },
  { name: "Venus", coords: "270,185,40", link: "/planets/venus" },
  { name: "Earth", coords: "300,195,40", link: "/planets/earth" },
  { name: "Mars", coords: "400,180,50", link: "/planets/mars" },
  { name: "Jupiter", coords: "425,180,50", link: "/planets/jupiter" },
  { name: "Saturn", coords: "520,180,50", link: "/planets/saturn" },
  { name: "Uranus", coords: "610,180,50", link: "/planets/uranus" },
  { name: "Neptune", coords: "690,180,50", link: "/planets/neptune" },
  { name: "Pluto", coords: "770,180,50", link: "/planets/pluto" },
];

const Planets = () => {
  const [coords, setCoords] = useState({ x: 0, y: 0 });

  const handleClick = (event) => {
    setCoords({ x: event.clientX, y: event.clientY });
  };

  return (
    <div className="planets-back" onClick={handleClick}>
      <p>Координати: X: {coords.x}, Y: {coords.y}</p>
      <img src={planetsGif} alt="Solar System" className="planets" useMap="#solarsystem" />
      <map name="solarsystem">
        {planets.map((planet) => (
          <area
            key={planet.name}
            shape="circle"
            coords={planet.coords}
            href={planet.link}
            alt={planet.name}
          />
        ))}
      </map>
    </div>
  );
};

export default Planets;
