import React from "react";
import "../style.css";
import { useEffect, useState } from "react";

const Main = () => {
  const text = "Welcome!";
  const typingSpeed = 200;
  const [displayedText, setDisplayedText] = useState("");
  const [index, setIndex] = useState(0); 

  useEffect(() => {
    if (index < text.length) {
      const timeout = setTimeout(() => {
        setDisplayedText((prev) => prev + text[index]);
        setIndex(index + 1); 
      }, typingSpeed);

      return () => clearTimeout(timeout);
    }
  }, [index, text]);

  return (
    <div className="main-container">
      <div className="welcome-section">
        <h1 className="welcome-text">{displayedText}</h1>
      </div>

      {/* Інша інформація, що з'являється під текстом "Welcome!" */}
      <div className="additional-info">
        <p>Тут буде інша інформація, що з'являється під "Welcome!"</p>
        <p>Наприклад, опис вашого сайту або інші важливі деталі.</p>
      </div>
    </div>
  );
};


export default Main;
