import React from "react";
import "../style.css";
import { useEffect, useState } from "react";

const Main = () => {
  const text = "Welcome!";
  const typingSpeed = 200;
  const [displayedText, setDisplayedText] = useState("");
  const [index, setIndex] = useState(0); 

  useEffect(() => {
    if (index < text.length) {
      const timeout = setTimeout(() => {
        setDisplayedText((prev) => prev + text[index]);
        setIndex(index + 1); 
      }, typingSpeed);

      return () => clearTimeout(timeout);
    }
  }, [index, text]);

  return (
    <div className="main-container">
      <div className="welcome-section">
        <h1 className="welcome-text">{displayedText}</h1>
      </div>
      <div className="additional-info">
        <h1>Chronology of the Universe</h1>
        {universeInfo.map((item, index) => (
          <div key={index} className="info-section">
            {/* Якщо індекс парний, то текст зліва, картинка справа */}
            <div className={`text-container ${index % 2 === 0 ? 'left' : 'right'}`}>
              <h2>{item.title}</h2>
              <p>{item.content}</p>
            </div>
            <div className="image-container">
              <img src={item.image} alt={item.title} className="info-image" />
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};



export default Main;


const universeInfo = [
  {
    title: 'Big Bang',
    content: 'The universe began from a singularity approximately 13.8 billion years ago.',
    image: '/assets/stars/b.png',
  },
  {
    title: 'Formation of Galaxies',
    content: 'After the Big Bang, matter began to clump together, forming galaxies.',
    image: '/assets/stars/b.png',
  },
  {
    title: 'The Solar System',
    content: 'About 4.6 billion years ago, the solar system formed around our Sun.',
    image: '/assets/stars/b.png',
  },
];
