import React, { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const Auth = () => {
  const [isRegistering, setIsRegistering] = useState(false);
  const [registerData, setRegisterData] = useState({
    username: "",
    email: "",
    password: "",
  });
  const [loginData, setLoginData] = useState({
    email: "",
    password: "",
  });

  const navigate = useNavigate();

  const register = async () => {
    try {
      const res = await axios.post("http://localhost:3001/register", registerData);
      localStorage.setItem("user", JSON.stringify(res.data));
      alert("Registration successful!");
      navigate("/forum");
    } catch (error) {
      console.error("Registration error:", error.response?.data || error.message);
    }
  };

  const login = async () => {
    try {
      const response = await axios.post("http://localhost:3001/login", loginData);
      setUser(response.data);  // Зберігаємо дані користувача, включаючи user_id
      localStorage.setItem("user", JSON.stringify(response.data)); // Зберігаємо дані користувача в localStorage
      navigate("/forum"); // Перехід на сторінку форуму після входу
    } catch (error) {
      console.error("Помилка при вході:", error.response?.data || error.message);
    }
  };

  // Оновлення значень для входу та реєстрації
  const [user, setUser] = useState(null);

  return (
    <div>
      <h3>{isRegistering ? "Реєстрація" : "Вхід"}</h3>

      {isRegistering ? (
        <>
          <input class = "login"
            type="text"
            placeholder="Ім'я"
            value={registerData.username}
            onChange={(e) => setRegisterData({ ...registerData, username: e.target.value })}
          />
          <input class = "login"
            type="email"
            placeholder="Електронна пошта"
            value={registerData.email}
            onChange={(e) => setRegisterData({ ...registerData, email: e.target.value })}
          />
          <input class = "login"
            type="password"
            placeholder="Пароль"
            value={registerData.password}
            onChange={(e) => setRegisterData({ ...registerData, password: e.target.value })}
          />
          <button className="back-button" onClick={register}>Зареєструватися</button>
        </>
      ) : (
        <>
          <input class = "login"
            type="email"
            placeholder="Електронна пошта"
            value={loginData.email}
            onChange={(e) => setLoginData({ ...loginData, email: e.target.value })}
          />
          <input class = "login"
            type="password"
            placeholder="Пароль"
            value={loginData.password}
            onChange={(e) => setLoginData({ ...loginData, password: e.target.value })}
          />
          <button className="back-button" onClick={login}>Увійти</button>
        </>
      )}

      <button className="btn-switch" onClick={() => setIsRegistering(!isRegistering)}>
        {isRegistering ? "Вже є акаунт? Увійти" : "Немає акаунту? Реєструйтесь"}
      </button>
    </div>
  );
};

export default Auth;
