import React from "react";
import "../style.css";
import { useEffect, useState } from "react";

const Moon = () => {
  const [phase, setPhase] = useState("");
  const [icon, setIcon] = useState("");

  useEffect(() => {
    fetch("https://devapi.qweather.com/v7/astronomy/moon") 
      .then((response) => response.json())
      .then((data) => {
        setPhase(data.phase); 
        updateIcon(data.phase);
      })
      .catch((error) => console.error("Error fetching moon phase:", error));
  }, []);

  const updateIcon = (phase) => {
    const icons = {
      "New Moon": "ðŸŒ‘",
      "First Quarter": "ðŸŒ“",
      "Full Moon": "ðŸŒ•",
      "Last Quarter": "ðŸŒ—",
      "Waxing Crescent": "ðŸŒ’",
      "Waxing Gibbous": "ðŸŒ”",
      "Waning Gibbous": "ðŸŒ–",
      "Waning Crescent": "ðŸŒ˜",
    };
    setIcon(icons[phase] || "ðŸŒ™");
  };

  return (
    <div>
      <h2>Moon Phase: {phase}</h2>
      <div style={{ fontSize: "50px" }}>{icon}</div>
    </div>
  );
};

export default Moon;
